import React, { Component } from "react";
import Contact from "./Contact";

class Contacts extends Component {
  constructor() {
    super();
    this.state = {
      aqib: [
        {
          id: "1",
          name: "John Deho",
          email: "saqib68@gmail.com",
          phone: "123-258-0525"
        },
        {
          id: "2",
          name: "Karen Smith",
          email: "Karan.Smith@gmail.com",
          phone: "123-258-0525"
        },
        {
          id: "3",
          name: "Jana Smith",
          email: "Jana.smith@gmail.com",
          phone: "123-258-0525"
        }
      ]
    };
  }
  systemPlaner = id => {
    const { aqib } = this.state;

    console.log(aqib);
    const newContact = aqib.filter(sam => sam.id !== id);

    this.setState({
      aqib: newContact
    });
  };
  render() {
    const { aqib } = this.state;
    return (
      <div>
        {aqib.map(sam => (
          <Contact
            key={sam.id}
            sam={sam}
            GamePlan={this.systemPlaner.bind(this, sam.id)}
          />
        ))}
      </div>
    );
  }
}

export default Contacts;
